// pgm with multiple attaribute

package com.codebind;

public class MultipleAttribute {
	
	String fName="John";
	String lName="Leo";
	int age=35;
	int sal=85000;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		MultipleAttribute obj= new MultipleAttribute();
		System.out.println("firstname="+obj.fName);
		System.out.println("lastname="+obj.lName);
		System.out.println("age="+obj.age);
		System.out.println("salary="+obj.sal);
		
		
	}

}
